import { Button, InputNumber, Modal } from "antd";
import { useRef, useState } from "react";

interface ISetting {
  setting: Array<number>;
  setSubmit: Function;
  handleClickSetting: Function;
}

interface IDialog {
  openPopup(): void;
  closePopup(): void;
}

export default function Setting(props: ISetting) {
  const { setting, setSubmit, handleClickSetting } = props;
  const [visible, setVisible] = useState(false);
  const settingFnc = () => {
    console.log("1111");
    setVisible((visible) => !visible);
    handleClickSetting();
  };
  const [rules, setRules] = useState({
    email: [
      { required: true, message: "请输入邮箱地址", trigger: "blur" },
      {
        type: "email",
        message: "请输入正确的邮箱地址",
        trigger: "blur,change",
      },
    ],
  });
  const onChange = (index: number) => {
    console.log(index);
  };
  return (
    <>
      <Button type="primary" onClick={settingFnc}>
        设置
      </Button>
      <Modal
        title="设置"
        visible={visible}
        onOk={() => {
          setVisible(false);
        }}
        onCancel={() => {
          setVisible(false);
        }}
        okText="确认"
        cancelText="取消"
      >
        {JSON.stringify(setting)}
        <p>第一行</p>
        {setting.map((item) => {
          return (
            <InputNumber
              min={1}
              max={10}
              defaultValue={3}
              onChange={onChange}
            />
          );
        })}
      </Modal>
    </>
  );
}
