import { useRef, useState } from "react";

interface ISetting {
  setting: Array<Array<number>>;
  setSubmit: Function;
  handleClickSetting: Function;
}

interface IDialog {
  openPopup(): void;
  closePopup(): void;
}

export default function Setting(props: ISetting) {
  const { setting, setSubmit, handleClickSetting } = props;
  const [dialogVisible, setDialogVisible] = useState(false);
  const settingFnc = () => {
    console.log("1111");
    setDialogVisible((dialogVisible) => !dialogVisible);
    handleClickSetting();
  };
  const [rules, setRules] = useState({
    email: [
      { required: true, message: "请输入邮箱地址", trigger: "blur" },
      {
        type: "email",
        message: "请输入正确的邮箱地址",
        trigger: "blur,change",
      },
    ],
  });
  const dialog = useRef(null);
  return (
    <>
      <Button type="warning" onClick={settingFnc}>
        设置
      </Button>
      <Dialog
        title="设置"
        visible={dialogVisible}
        ref={dialog}
        onCancel={() => setDialogVisible(!dialogVisible)}
      >
        <Dialog.Footer className="dialog-footer">
          <Button onClick={() => setDialogVisible(false)}>取消</Button>
          <Button
            type="primary"
            onClick={() => {
              setDialogVisible(false);
            }}
          >
            确定
          </Button>
        </Dialog.Footer>
      </Dialog>
    </>
  );
}
